mapper.select:
select

  distinct


from t_integral_get_record

  


  order by ${orderByClause}


	limit   ${start},${end}

select 

from t_integral_get_record
where id = #{id,jdbcType=BIGINT}
  and sup_user_id = #{supUserId,jdbcType=BIGINT}

mapper.delete:
delete from t_integral_get_record
where id = #{id,jdbcType=BIGINT}
  and sup_user_id = #{supUserId,jdbcType=BIGINT}

mapper.insert:
insert into t_integral_get_record (id, sup_user_id, user_id, 
  integral, get_date_time, record_id, 
  valid_date, has_used_integral, not_used_integral, 
  rule_id, discount, ava_date_time
  )
values (#{id,jdbcType=BIGINT}, #{supUserId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, 
  #{integral,jdbcType=BIGINT}, #{getDateTime,jdbcType=TIMESTAMP}, #{recordId,jdbcType=BIGINT}, 
  #{validDate,jdbcType=DATE}, #{hasUsedIntegral,jdbcType=BIGINT}, #{notUsedIntegral,jdbcType=BIGINT}, 
  #{ruleId,jdbcType=BIGINT}, #{discount,jdbcType=DECIMAL}, #{avaDateTime,jdbcType=TIMESTAMP}
  )
